INSERT INTO groups (name, user_id, category_id, description, photo) VALUES 
('Data Mesh Learning Meetup Global', 6, 1, 'We are a vendor-independent group dedicated to helping people learn and find resources about data mesh. We have partnered with Zhamak to help people get up to speed without being sold to. We plan to have at least one meetup per month in each a European-friendly time and a US-friendly time. We will be posting our content to YouTube if you cant make it live.', 'https://www.avanade.com/-/media/images/blogs/tech-and-specs/from-data-lakes-to-data-mesh.jpg?la=en&ver=2'),
('The Trail Blazers', 6, 2, 'Lets Explore the Nature! Most hikes will be around GTA, but wont be limited to that. If you want to hike and love any particular hike, please feel free to post a suggestion in the group.', 'http://travelwiththesmile.com/wp-content/uploads/2017/09/Banff-hikes-20-best-hikes-in-Banff-National-Park-Canada-Castle-Mountainn.jpg'),
('GTA Moms of Babies, Toddlers & Preschool', 6, 3, 'Meet other moms, dads, and caregivers living in GTA  to have play dates, get new mommy advice, find out about all the word of mouth programs, places and destinations. Enjoy exclusive events only for UWS Moms with Gymboree, Apple Seeds, and Equinox plus other exciting partnerships. We are also your best resource for $10-15.00 drop in music and language classes on the UWS!', 'https://www.babycenter.com/ims/2020/04/iStock-947850390_wide.jpg'),
('The Salsa Latin & Ballroom Dance Group, Toddlers & Preschool', 6, 4, 'Our group goes out to social dance events & clubs in GTA  and surrounding areas. Suggestions for future events are also appreciated. No prior dance experience is necessary, just a great attitude!', 'https://img.evbuc.com/https%3A%2F%2Fcdn.evbuc.com%2Fimages%2F142873241%2F101578785739%2F1%2Foriginal.20210728-014112?w=512&auto=format%2Ccompress&q=75&sharp=10&rect=1%2C815%2C5802%2C2901&s=728b69fd0c09d00ecfde6ef75ff132fd'),
('Eclectic Music Events & More', 6, 4, 'Do you like great live music, dancing, dinners, movies, shows AND like to walk/hike? We do it all. We offer an Eclectic mix of recreational, cultural and various social activities.', 'https://eturbonews.com/wp-content/uploads/2018/01/rock-concert.jpg');